	program gauss	double precision x,del,pi2,f,dens,c	character*20 cfname,hfname	write(*,*)'input .cdf filename'	read(*,'(a)')cfname	open(unit=10,file=cfname)	write(*,*)'input .g filename'	read(*,'(a)')hfname	open(unit=12,file=hfname)	write(*,*)'input mean, std'	read(*,*) xm,std	xmin=xm-4*std	xmax=xm+4*std	xsum=0	min=xmin	max=xmax	c=0	do 100 i=min,max	do 75 j=1,10	x=i+.1*j	del=x-xm	pi2=2*3.14159265	exp=-(del*del)/(2*std*std)	f=1/(std*(sqrt(pi2)))	dens=f*(2.67**exp)	c=c+.1*dens	write(10,*)x,c	write(12,*)x,dens 75	continue 100	continue 	close(unit=10)c 	close(unit=12)	stop	end